        if (trackedProcess.executionStatus_code == null) {
			obj.executionStatus_code = "NOT_STARTED";
		}
        
        updateTPProperties(trackedProcess, obj);
        needForward = false;
        
    } else if (trackedProcess.executionStatus_code != "COMPLETED") {
        if ("com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.GoodsReceipt" == actualEvent.eventType || 
			"com.lbngttsamples.gtt.app.pof.Shipment.POD" == actualEvent.eventType) {
            ?"===> entering Shipment.POD or PurchaseOrderItem.GoodsReceipt of InboundDeliveryItem";
            var plannedEvents = trackedProcess.plannedEvents;
            var allShipmentPODReported = true; 
            var found = false;
            var i = 0;
            while (i < plannedEvents.size() && allShipmentPODReported) {
                if (plannedEvents[i].eventType == "com.lbngttsamples.gtt.app.pof.Shipment.POD") {
                    found = true;
                    if (plannedEvents[i].eventStatus_code != "REPORTED" 
                        && plannedEvents[i].eventStatus_code != "EARLY_REPORTED" 
                        && plannedEvents[i].eventStatus_code != "LATE_REPORTED") {
                    
                        allShipmentPODReported = false;
                    }    
                }
                i = i + 1;
            }
            
            if (allShipmentPODReported && found) {
                obj = toObject("{}");
                obj.executionStatus_code = "COMPLETED";
                updateTPProperties(trackedProcess, obj); 
            } 
			needForward = true;
        } 
		
		if (trackedProcess.executionStatus_code != "COMPLETED" && 
				("com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.PutAway" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.Packing" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.GoodsReceipt" == actualEvent.eventType || 
