                "com.lbngttsamples.gtt.app.pof.Shipment.LoadingStart" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.pof.Shipment.LoadingEnd" == actualEvent.eventType || 
				"com.lbngttsamples.gtt.app.pof.Shipment.CheckIn" == actualEvent.eventType || 
				"com.lbngttsamples.gtt.app.pof.Shipment.POD" == actualEvent.eventType)) {
            
            ?"===> entering other events of Inbound Delivery Item without Delay and LocationUpdate";    
            obj = toObject("{}");
            obj.executionStatus_code = "IN_TRANSIT";
            updateTPProperties(trackedProcess, obj); 
            needForward = true;
        }
        
    } 
    
    if ("com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.DeletionEvent" == actualEvent.eventType || 
			"com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.UndeletionEvent" == actualEvent.eventType || 
			"com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.ConfirmationEvent" == actualEvent.eventType ||  
			"com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.GoodsReceipt" == actualEvent.eventType || 
			"com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.PutAway" == actualEvent.eventType || 
