            "com.lbngttsamples.gtt.app.pof.Shipment.Arrival" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.LoadingStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.LoadingEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.POD" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.GateInStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.GateInEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.GateOutStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.GateOutEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.UnloadingStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.UnloadingEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.Stuffing" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.UnStuffing" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.Return" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.pof.Shipment.POPU" == actualEvent.eventType || 
