            obj.lastReportedEvent = lastCorrelatedEvent.eventType;
            updateTPProperties(trackedProcess, obj);
		}
							
        if (trackedProcessBeforeEventPost != null && trackedProcessBeforeEventPost.processStatus_code != trackedProcess.processStatus_code) {
            ?"===>processStatus changed to";
            ?trackedProcess.processStatus_code;
        }
		needForward = true;
    }
    
    ?"==>InboundDeliveryItem done.";
    
    
} else if (trackedProcess != null && "com.lbngttsamples.gtt.app.pof.Shipment.Shipment" == trackedProcess.trackedProcessType) {
	?"entering Shipment processing";
    if ("com.lbngttsamples.gtt.app.pof.Shipment.ShipmentEvent" == actualEvent.eventType) {
        ?"===> entering ShipmentEvent";
		if (trackedProcess.executionStatus_code == null) {
			obj = toObject("{}");
			obj.executionStatus_code = "NOT_STARTED";
			updateTPProperties(trackedProcess, obj);
			
		} 
    } else if ("com.lbngttsamples.gtt.app.pof.Shipment.POD" == actualEvent.eventType || 
			   "com.lbngttsamples.gtt.app.pof.Shipment.Arrival" == actualEvent.eventType)  {
		?"===> entering Shipment.POD";	
		if (trackedProcess.executionStatus_code != "COMPLETED") {
			var plannedEvents = trackedProcess.plannedEvents;
            var allShipmentPODAndArrivalReported = true; 
            var found = false;
            var i = 0;
            while (i < plannedEvents.size() && allShipmentPODAndArrivalReported) {
                if (plannedEvents[i].eventType == "com.lbngttsamples.gtt.app.pof.Shipment.POD" || 
					plannedEvents[i].eventType == "com.lbngttsamples.gtt.app.pof.Shipment.Arrival") {
